# Blueprint routes
@home_remedies.route('/')
def remedies_home():
    return render_template("home_remedies.html", remedies=list(remedies.keys()))

@home_remedies.route('/get_remedy', methods=['POST'])
def get_remedy():
    selected_ailment = request.form.get('disease')
    remedy = remedies.get(selected_ailment, 'No remedy available.')
    return render_template("home_remedies.html", 
                         remedies=list(remedies.keys()), 
                         selected_ailment=selected_ailment, 
                         remedy=remedy)

# Create Flask App
app = Flask(__name__)

# Register Blueprint
app.register_blueprint(home_remedies, url_prefix='/home_remedies')

# Main app route
@app.route('/')
def home():
    return render_template('index.html')

if __name__ == '__main__':
    app.run(debug=True)